muclient.script:
function OnPluginBroadcast (msg, id, name, text) phelper:OnPluginBroadcast(msg, id, name, text) if id == '3e7dedbe37e44942dd46d264' then if text:find("comm.quest") then res, gmcparg = CallPlugin("3e7dedbe37e44942dd46d264","gmcpval","comm.quest") stuff = assert (loadstring ('return ' .. gmcparg or ""))()
if stuff.action == 'ready' then phelper:broadcast(1) check(EnableTimer("questtimer", false)) elseif stuff.action == 'start' then phelper.tquest = blankquest() phelper.tquest['starttime'] = GetInfo(304) phelper.tquest['mobname'] = stuff.targ phelper.tquest['mobroom'] = stuff.room phelper.tquest['mobarea'] = stuff.area var.quest_info = serialize.save( "quest_info", phelper.tquest ) phelper:broadcast(2, var.quest_info) questor_time (tonumber(stuff.timer)) SaveState() elseif stuff.action == 'killed' then check(EnableTimer("questtimer", false)) phelper:broadcast(4) elseif stuff.action == 'comp' then phelper.tquest['qp'] = stuff.qp phelper.tquest['gold'] = stuff.gold phelper.tquest['tierqp'] = stuff.tierqp phelper.tquest['lucky'] = stuff.lucky phelper.tquest['mccp'] = stuff.mccp phelper.tquest['tp'] = stuff.tp phelper.tquest['trains'] = stuff.trains phelper.tquest['pracs'] = stuff.pracs phelper.tquest['finishtime'] = GetInfo(304) phelper.tquest['double'] = 0 phelper.tquest['daily'] = 0 phelper.tquest['hardcore'] = stuff.hardcore phelper.tquest['opk'] = stuff.opk phelper.tquest['totqp'] = tonumber(stuff.totqp) if tonumber(stuff.double) == 1 then phelper.tquest['double'] = 1 end if tonumber(stuff.daily) == 1 then phelper.tquest['daily'] = 1 end var.quest_info = serialize.save( "quest_info", phelper.tquest ) phelper:broadcast(6, var.quest_info) when_required = os.time () + (30 * 60) -- 30 mins to go onquest = false update_timer() SaveState() elseif stuff.action == 'fail' or stuff.action == 'timeout' then when_required = os.time () + (stuff.wait * 60) -- 15 mins to go onquest = false update_timer() phelper.tquest.failed = 1 phelper.tquest['finishtime'] = GetInfo(304) var.quest_info = serialize.save( "quest_info", phelper.tquest ) phelper:broadcast(8, var.quest_info) SaveState() elseif stuff.action == 'status' then if stuff.status == 'ready' then quest_available() elseif stuff.wait then when_required = os.time() + (stuff.wait * 60) update_timer() elseif stuff.target == 'killed' then phelper:broadcast(4) elseif stuff.target == 'missing' then
else onquest = true phelper.tquest['starttime'] = GetInfo(304) phelper.tquest['mobname'] = stuff.targ phelper.tquest['mobroom'] = stuff.room phelper.tquest['mobarea'] = stuff.area var.quest_info = serialize.save( "quest_info", phelper.tquest ) phelper:broadcast(2, var.quest_info) questor_time (tonumber(stuff.timer)) SaveState() end elseif stuff.action == 'reset' then when_required = os.time() + (stuff.timer * 60) update_timer() end end end end
function OnPluginInstall () --OnPluginEnable is automatically called by pluginhelper
phelper:OnPluginInstall() end -- OnPluginInstall
function OnPluginClose ()
phelper:OnPluginClose() end -- OnPluginClose
function OnPluginEnable () ldplugin ("aard_GMCP_handler", "3e7dedbe37e44942dd46d264")
phelper:OnPluginEnable()
if IsConnected() then CallPlugin("3e7dedbe37e44942dd46d264","Send_GMCP_Packet","request quest") end
end -- OnPluginEnable
function OnPluginDisable ()
phelper:OnPluginDisable() end -- OnPluginDisable
function OnPluginConnect ()
phelper:OnPluginConnect() end -- function OnPluginConnect
function OnPluginDisconnect ()
phelper:OnPluginDisconnect() end -- function OnPluginConnect
function OnPluginSaveState ()
phelper:OnPluginSaveState() end -- function OnPluginSaveState
